import * as React from "react";
import Checkbox from "@mui/material/Checkbox";
import { updateTask } from "./until";
import HomePage from "..";
import axios from "axios";

export default function ControlledCheckbox({ id, status, newPost }: any) {
  const [checked, setChecked] = React.useState(status);
  console.log("newPost", newPost);

  const handleChangeCheckBox = async (e) => {
    setChecked(e.target.checked);
    // status = newPost;

    try {
      await updateTask(id, status);
    } catch (error) {
      console.log("error", error);
    }
    // console.log("statusss", status);
  };

  const handleUpdatePost = (newPost) => {
    if(newPost.id === id) {
      newPost.status = true
    }

    console.log("abc", newPost.id);

  };

  return (
    <div>
      <Checkbox
        checked={checked}
        onChange={handleChangeCheckBox}
        onClick={handleUpdatePost}
        inputProps={{ "aria-label": "controlled" }}
      />
    </div>
  );
}
