import axios from "axios";

export const updateTask = async (id: any, status: any) => {

  const res = await axios.put(`https://api-nodejs-todolist.herokuapp.com/task/${id}`,
    {
        "completed": status,
    },
    {
      headers: {
        "Content-Type": "application/json",
        Authorization:
        `${localStorage.token}`,
      },
    }
  );

  return res;
};
