import * as React from 'react';
import Checkbox from '@mui/material/Checkbox';
import { updateTask } from './until';

export default function ControlledCheckbox(props: any) {
  const { id , status } = props
  const [checked, setChecked] = React.useState(status);  

  const handleChange = async (event: React.ChangeEvent<HTMLInputElement>) => {
    setChecked(event.target.checked);

    console.log('event' ,event);

    try {
      const response = await updateTask(id, status);

      // console.log("response file index", response.data.data.completed);

      // console.log("boolean", boolean);

      // setChecked(boolean);
      // console.log('id' ,id);
      // console.log('stt', status)

    } catch (error) {
      console.log('error', error);
    }
  };

  return (
    <Checkbox
      checked={checked}
      onChange={handleChange}
      inputProps={{ 'aria-label': 'controlled' }}
    />
  );
}
